INSERT INTO careers (title, slug, description, detailed_description, category, subcategory, education_level, skills_required, salary_range_min, salary_range_max, location, company_types, growth_prospects, opportunities_in_jk, requirements, career_path, demand_score, featured, tags) VALUES

('Software Engineer', 'software-engineer', 'Develop innovative software solutions for the growing tech sector in J&K', 'Software Engineers design, develop, and maintain software applications, systems, and platforms. In Jammu & Kashmir, the IT sector is rapidly expanding with government initiatives supporting technology development. You''ll work on everything from mobile apps to enterprise software, contributing to digital transformation initiatives across the region.', 'Technology', 'Software Development', 'Bachelor''s Degree', '["Programming", "Problem Solving", "Teamwork", "Communication", "Analytical Thinking", "Java", "Python", "JavaScript"]', 400000, 1500000, '["Srinagar", "Jammu", "Remote"]', '["IT Companies", "Startups", "Government", "E-commerce", "Consulting"]', 'Excellent growth potential with the government''s push for digital J&K. New IT parks and incubation centers are creating numerous opportunities.', 'Growing IT sector with companies like TCS, Infosys setting up offices. Government initiatives like Digital India and startup incubation programs provide excellent opportunities.', 'Bachelor''s degree in Computer Science, IT, or related field. Strong programming skills in modern languages. Understanding of software development lifecycle.', '["Junior Developer", "Software Engineer", "Senior Developer", "Tech Lead", "Engineering Manager", "CTO"]', 9, true, '["technology", "programming", "software", "IT", "digital"]'),

('Medical Doctor', 'medical-doctor', 'Provide essential healthcare services in J&K''s expanding healthcare system', 'Medical Doctors diagnose, treat, and care for patients across various specializations. J&K has a growing healthcare infrastructure with new medical colleges and hospitals being established. The region offers opportunities in both public and private healthcare sectors, with special focus on community health and medical tourism.', 'Healthcare', 'Medicine', 'MBBS + MD/MS', '["Medical Knowledge", "Empathy", "Communication", "Problem Solving", "Leadership", "Patient Care", "Clinical Skills"]', 700000, 3000000, '["Srinagar", "Jammu", "Leh", "Anantnag", "Rajouri"]', '["Government Hospitals", "Private Hospitals", "Clinics", "Medical Colleges", "Research Institutes"]', 'Excellent prospects with government healthcare expansion, new medical colleges, and growing medical tourism sector.', 'High demand due to doctor shortage. New AIIMS, medical colleges being established. Medical tourism potential in regions like Gulmarg and Pahalgam.', 'MBBS degree followed by MD/MS specialization. Clear NEET-UG and NEET-PG examinations. State medical council registration.', '["Medical Student", "Junior Resident", "Senior Resident", "Consultant", "Department Head", "Medical Director"]', 10, true, '["healthcare", "medicine", "doctor", "medical", "patient care"]'),

('Tourism Manager', 'tourism-manager', 'Lead J&K''s booming tourism industry with strategic management skills', 'Tourism Managers oversee tourism operations, develop travel packages, and ensure excellent visitor experiences. J&K''s tourism industry is experiencing unprecedented growth with government focus on promoting the region as a premier destination. This role involves working with hotels, travel agencies, and government tourism departments.', 'Tourism & Hospitality', 'Tourism Management', 'Bachelor''s Degree', '["Management", "Customer Service", "Leadership", "Communication", "Marketing", "Cultural Knowledge", "Event Planning"]', 350000, 1200000, '["Srinagar", "Gulmarg", "Pahalgam", "Leh", "Jammu", "Sonamarg"]', '["Tourism Companies", "Hotels", "Resorts", "Government Tourism", "Travel Agencies"]', 'Exceptional growth with tourism revival, new infrastructure projects, and international recognition of J&K destinations.', 'Tourism is booming with record visitor numbers. Government investments in infrastructure, new destinations being developed, and adventure tourism growing rapidly.', 'Bachelor''s in Tourism Management, Hotel Management, or related field. Knowledge of local culture, languages, and tourism products.', '["Tourism Executive", "Assistant Manager", "Manager", "Senior Manager", "General Manager", "Regional Director"]', 8, true, '["tourism", "hospitality", "travel", "management", "customer service"]'),

('Data Scientist', 'data-scientist', 'Drive data-driven decisions in J&K''s emerging digital economy', 'Data Scientists analyze large datasets to derive insights for business and governance decisions. With J&K''s focus on digital governance, smart agriculture, and data-driven policy making, there''s growing demand for professionals who can work with government data, agricultural analytics, and business intelligence.', 'Technology', 'Data Science', 'Master''s Degree', '["Statistics", "Machine Learning", "Python", "R", "SQL", "Data Visualization", "Analytics", "Problem Solving"]', 600000, 2500000, '["Srinagar", "Jammu", "Remote"]', '["Government", "IT Companies", "Research Institutes", "Startups", "Consulting"]', 'High demand expected with digital J&K initiatives, smart city projects, and data-driven governance focus.', 'Government initiatives for digital J&K, smart agriculture projects, and governance analytics creating new opportunities.', 'Master''s in Data Science, Statistics, Computer Science, or related field. Strong background in mathematics and programming.', '["Data Analyst", "Junior Data Scientist", "Data Scientist", "Senior Data Scientist", "Lead Data Scientist", "Chief Data Officer"]', 9, true, '["data science", "analytics", "machine learning", "statistics", "technology"]'),

('Agricultural Engineer', 'agricultural-engineer', 'Modernize J&K''s agriculture with innovative technology solutions', 'Agricultural Engineers develop and implement technology solutions for farming. J&K''s agriculture sector is modernizing with focus on sustainable farming, precision agriculture, and food processing. This role involves working on irrigation systems, farm machinery, and agricultural technology.', 'Agriculture', 'Agricultural Technology', 'Bachelor''s Degree', '["Engineering", "Agricultural Technology", "Project Management", "Problem Solving", "Technical Skills", "Innovation"]', 300000, 1000000, '["Srinagar", "Jammu", "Rural Areas", "Agricultural Universities"]', '["Government", "Agricultural Companies", "NGOs", "Research Institutes", "Private Farms"]', 'Good prospects with focus on agricultural modernization, precision farming, and sustainable agriculture initiatives.', 'Government support for modern agriculture, drip irrigation projects, food processing units, and agricultural technology adoption.', 'B.Tech in Agricultural Engineering or related field. Knowledge of modern agricultural practices and technology.', '["Junior Engineer", "Agricultural Engineer", "Senior Engineer", "Project Manager", "Chief Engineer", "Technical Director"]', 7, false, '["agriculture", "engineering", "technology", "farming", "rural development"]'),

('School Teacher', 'school-teacher', 'Shape young minds and contribute to educational excellence in J&K', 'School Teachers educate and mentor students across primary, secondary, and higher secondary levels. J&K is investing heavily in education infrastructure with new schools, teacher training programs, and digital education initiatives creating opportunities for dedicated educators.', 'Education', 'Teaching', 'Bachelor''s + B.Ed', '["Subject Knowledge", "Communication", "Patience", "Classroom Management", "Empathy", "Leadership", "Innovation"]', 250000, 700000, '["Throughout J&K", "Urban Areas", "Rural Areas"]', '["Government Schools", "Private Schools", "Coaching Institutes", "Online Education"]', 'Stable career with social impact. Regular recruitment, promotion opportunities, and digital education integration.', 'High demand for quality teachers, new schools being established, digital education initiatives, and teacher training programs.', 'Bachelor''s degree in relevant subject plus B.Ed qualification. Clear Teacher Eligibility Test (TET) and state-level examinations.', '["Student Teacher", "Assistant Teacher", "Teacher", "Senior Teacher", "Vice Principal", "Principal"]', 8, false, '["education", "teaching", "student development", "academic", "learning"]'),

('Digital Marketing Specialist', 'digital-marketing-specialist', 'Drive online presence for J&K businesses in the digital age', 'Digital Marketing Specialists create and manage online marketing campaigns for businesses. With J&K''s growing digital economy and e-commerce adoption, businesses need professionals who understand digital marketing, social media, and online brand management.', 'Marketing', 'Digital Marketing', 'Bachelor''s Degree', '["Digital Marketing", "Social Media", "Content Creation", "Analytics", "SEO", "Communication", "Creativity"]', 300000, 1200000, '["Srinagar", "Jammu", "Remote"]', '["Marketing Agencies", "E-commerce", "Startups", "Corporate", "Freelance"]', 'Excellent growth with increasing digital adoption, e-commerce growth, and business digitalization.', 'Growing demand as businesses go digital, e-commerce platforms expand, and tourism sector embraces digital marketing.', 'Bachelor''s degree in Marketing, Communications, or related field. Certifications in Google Ads, Facebook Marketing, and analytics tools.', '["Marketing Assistant", "Digital Marketing Executive", "Specialist", "Manager", "Head of Marketing", "CMO"]', 8, true, '["marketing", "digital", "social media", "advertising", "online", "branding"]'),

('Civil Engineer', 'civil-engineer', 'Build J&K''s infrastructure for sustainable development', 'Civil Engineers design and oversee construction of infrastructure projects including roads, bridges, buildings, and water systems. J&K is witnessing massive infrastructure development with new highways, tunnels, bridges, and smart city projects creating numerous opportunities.', 'Engineering', 'Civil Engineering', 'Bachelor''s Degree', '["Engineering Design", "Project Management", "Technical Skills", "Problem Solving", "Leadership", "Safety Management"]', 400000, 1500000, '["Srinagar", "Jammu", "Project Sites", "Urban Areas"]', '["Government", "Construction Companies", "Consulting Firms", "Infrastructure Companies"]', 'Excellent prospects with ongoing infrastructure projects, smart city initiatives, and connectivity improvements.', 'Major infrastructure projects like Delhi-Katra expressway, various tunnels, bridges, and smart city developments creating opportunities.', 'B.Tech in Civil Engineering. Professional engineering license preferred. Knowledge of latest construction technologies and software.', '["Junior Engineer", "Engineer", "Senior Engineer", "Project Manager", "Chief Engineer", "Director"]', 9, true, '["engineering", "infrastructure", "construction", "civil", "development", "projects"]'),

('Hotel Manager', 'hotel-manager', 'Lead hospitality excellence in J&K''s premium tourism destinations', 'Hotel Managers oversee all aspects of hotel operations ensuring exceptional guest experiences. With J&K''s tourism boom and new luxury properties being developed, there''s high demand for experienced hospitality professionals who understand service excellence.', 'Tourism & Hospitality', 'Hotel Management', 'Bachelor''s Degree', '["Management", "Customer Service", "Leadership", "Communication", "Problem Solving", "Operations", "Team Building"]', 400000, 1800000, '["Srinagar", "Gulmarg", "Pahalgam", "Leh", "Jammu"]', '["Hotels", "Resorts", "Hospitality Chains", "Boutique Properties", "Government Tourism"]', 'Excellent growth with tourism revival, new hotel projects, and premium hospitality segment expansion.', 'Tourism industry booming with new luxury hotels, resorts being developed, and government focus on hospitality sector growth.', 'Bachelor''s in Hotel Management, Tourism, or related field. Experience in hospitality operations and customer service.', '["Management Trainee", "Assistant Manager", "Manager", "General Manager", "Regional Manager", "VP Operations"]', 8, true, '["hospitality", "hotel", "management", "tourism", "customer service", "operations"]');

